import React, { Component } from 'react';
import logo from './logo.svg';
import './App.css';

class App extends Component {

  state = {counter:0, running:false};
  interval = {};

  startEvent = () => {


    var time = 0;   

        if(this.state.running == false) {

           this.interval = setInterval(() => {
            time = time+1;
            this.setState({counter:time});   
            });

        } else {
            stopEvent();
        }



} 

    stopEvent = () => {
        this.setState({counter:0,running:false});
        clearInterval(this.interval);
    }

  render() {

    return (
      <div>
      <label>{element.counter}ms</label>
      <button onClick={this.startEvent}>Start</button>
      <button onClick={this.stopEvent}>Stop</button>
  </div>
    );
  }
}

export default App;
